import{_ as l,c as a,o as i,V as e,a4 as t,a5 as r}from"./chunks/framework.szLWUyCm.js";const b=JSON.parse('{"title":"二、上网剖析","description":"","frontmatter":{},"headers":[],"relativePath":"computer/base/上网剖析.md","filePath":"computer/base/上网剖析.md","lastUpdated":null}'),o={name:"computer/base/上网剖析.md"},s=e('<h1 id="二、上网剖析" tabindex="-1">二、上网剖析 <a class="header-anchor" href="#二、上网剖析" aria-label="Permalink to &quot;二、上网剖析&quot;">​</a></h1><h2 id="一、bs上网剖析" tabindex="-1">一、BS上网剖析 <a class="header-anchor" href="#一、bs上网剖析" aria-label="Permalink to &quot;一、BS上网剖析&quot;">​</a></h2><p><img src="'+t+'" alt="图片"></p><hr><ol><li>浏览器：输入网址请求服务器</li><li>服务器：会把注册页面通过网络发送给浏览器</li><li>浏览器：接收到数据后展示页面上</li><li>浏览器：用户输入用户名和密码，通过网络以固定的格式发送给服务器</li><li>服务器： <ul><li>接收数据</li><li>解析数据得到用户名和密码</li><li>将正确的信息存入数据库</li><li>将结果发送给浏览器</li></ul></li></ol><h2 id="二、cs上网剖析" tabindex="-1">二、CS上网剖析 <a class="header-anchor" href="#二、cs上网剖析" aria-label="Permalink to &quot;二、CS上网剖析&quot;">​</a></h2><p><img src="'+r+'" alt="图片"></p><hr><ol><li>客户端：打开之后直接输入用户名密码，通过网络以固定的格式发送给服务器</li><li>服务端： <ul><li>接收数据</li><li>解析数据得到用户名和密码</li><li>获取数据库正确的信息加以对比</li><li>将结果发送给客户端</li></ul></li></ol><h2 id="三、bs和cs的对比" tabindex="-1">三、BS和CS的对比 <a class="header-anchor" href="#三、bs和cs的对比" aria-label="Permalink to &quot;三、BS和CS的对比&quot;">​</a></h2><ol><li><p>BS中，在你没网的时候，打开浏览器是不会显示页面的</p><p>CS中，打开客户端，就算没网，最开始的登录页面还是可以打开的，但是不能成功登录</p></li><li><p>BS架构：维护方便，开发简单且共享性强，成本低</p><p>对服务器要求过高，数据传输速度慢，很难实现复杂的页面效果</p></li><li><p>CS架构：交互性强、具有安全的存取模式，网络通信量低，利于处理大量数据</p><p>在客户端可以展示很复杂的页面效果</p><p>充分利用两端硬件，将任务分配到Client和Server两端，降低了系统的通讯开销</p></li></ol>',11),c=[s];function p(_,n,h,d,u,m){return i(),a("div",null,c)}const f=l(o,[["render",p]]);export{b as __pageData,f as default};
